import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-auth',
  templateUrl: './auth.component.html',
  styleUrls: ['./auth.component.scss']
})
export class AuthComponent implements OnInit {
  password: string | undefined;
  login: string | undefined;
  message: string = 'Déconnecté';




  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }

  login(){
    const isLogged =this.authSrv.login(this.login, this.password);
    if(isLogged) {
      this.router.navigate(['']);
    }else {
      this.message ='Utilisateur ou mot de passe incorect';
    }
  }


}
<div>
    <h3>Se connecter</h3>
    <div>
<form #loginForm="ngForm">
    <div>
        <label for="login">Votre Login</label>
        <input type="text" name="login" id="login" [(ngModel)]="login" required>
    </div>
    <div>
        <label for="password">Votre mot de passe</label>
        <input type="password" name="password" id="password" [(ngModel)]="password" required>
    </div>
    <div>
        <button [disabled]="!loginForm.form.valid" (click)="login()">Se connecter</button>
    </div>
</form>
    </div>
</div>
