import { Injectable } from "@angular/core";
import { HttpClient } from "@angular/common/http";
import { Observable } from "rxjs";
import { Worker } from "../models/worker.model";


@Injectable({
    providedIn: 'root'
})

export class WorkerService{
    constructor(private http: HttpClient){}


    //Liste travailleur
    getWorkerList(): Observable<any>{
        return this.http.get<any>('http://localhost:3000/api/personnes');
    }

    //Creation
    createWorker(worker : Worker): Observable<any> {
        return this.http.post<any>('http://localhost:3000/api/personnes',Worker);
    }

    //Delete
    deleteWorker(): Observable<any> {
        return this.http.delete<any>('http://localhost:3000/api/personnes');
    }

    // //mise à jour
