import { Injectable } from "@angular/core";
import { HttpClient, HttpHeaders } from "@angular/common/http";
import { Observable } from "rxjs";
import { Worker } from "../models/worker.model";

const httpOptions = {
    headers: new HttpHeaders({
        'content-type': 'application/json',
        'Access-Control-Allow-Origin': '*',
    })
}
@Injectable({
    providedIn: 'root'
})

export class WorkerService{
    constructor(private http: HttpClient){}


    //Liste travailleur
    getWorkerList(): Observable<any>{
        return this.http.get<any>('http://localhost:3000/api/personnes');
    }

    //Creation
    createWorker(worker : Worker): Observable<Worker> {
        console.log(worker);
        return this.http.post<Worker>('http://localhost:3000/api/personnes',worker, httpOptions); 
    }

    //Delete
    deleteWorker(): Observable<any> {
        return this.http.delete<any>('http://localhost:3000/api/personnes');
    }

    // //mise à jour
    // updateWorker(): Observable<any> {
    //     return this.http.post<any>('http://localhost:3000/api/personnes',WorkerService);
    // }
   
}
